import React, { Component } from 'react';
import './camera.css';
import { auth } from 'firebase';
import openSocket from 'socket.io-client';

class Camera extends Component {
  constructor(props) {
    super(props);
    this.state = {
      socket: openSocket('http://localhost:1337'),
      img: null,
      AutoMode: false,
      HeadTurn: 'Right',
      HeadDegree: 0,
      BodyDegree: 0,
      MovementDegree: 0,
      RealHead: 0,
      RealBody: 0,
      RealMovement: 0,
      BodyTurn: 'Right',
      Movement: 'Right'
    }

    let self = this
    this.state.socket.on('image', curImg => {
      this.setState({img: curImg})
    });
  }

  ChangeAutoMode = () => {
    this.setState({AutoMode: !this.state.AutoMode});
  }

  render() {
    var url = this.state.img ? ("data:image/jpg;base64," + this.state.img) : null;
      return (
        <div className="container">
          <div className="row dashboard">
                  <div className="col xs12 m6 image">
                    <img src={url} />
                  </div>
                  <div className="col m4 push-m1 button-container">
                    <div className="row"><button onClick ={this.ChangeAutoMode} className="auto">Auto {this.state.AutoMode ? "On" : "Off"}</button></div>

                    <div className="row"><button>Tracking</button></div>
                    <div className="row"><button>Follow Link</button></div>
                    <div className="row"><button>Up</button></div>
                    <div className="row"><button>Left</button></div>
                    <div className="row"><button>Right</button></div>
                    <div className="row"><button>Down</button></div>
                  </div>
                  
          </div>
        </div>
      )
  }
}

export default Camera;
