import React, { Component } from 'react';
import './camera.css';
import { auth } from 'firebase';
import openSocket from 'socket.io-client';

class Camera extends Component {
  constructor(props) {
    super(props);
    this.state = {
      socket: openSocket('http://localhost:1337'),
      
      img: null,

      AutoMode: false,
      HeadDir: "L",
      HeadDeg: 0,
      BodyDir: "L",
      BodyDeg: 0,
      Dist: 0,
      MovtDir: "L",
      MovtDeg: 0,

      LinkX: 0,
      LinkY: 0,
      LaserX: 0,
      LaserY: 0
    }

    let self = this
    this.state.socket.on('image', curImg => {
      this.setState({img: curImg})
    });
  }

  /* Motion Controls */
  ChangeAutoMode = () => {
    this.setState({AutoMode: !this.state.AutoMode});
  }

  /* Head */
  handleHead = (e) => {
    this.setState({HeadDeg: e.target.value});
  }
  handleHeadDir = (e) => {
    this.setState({HeadDir: e.target.value});
  };

  /* Body */
  handleBody = (e) =>{
    this.setState({BodyDeg: e.target.value});
  }
  handleBodyDir = (e) => {
    this.setState({BodyDir: e.target.value});
  };

  handleDist = (e) =>{
    this.setState({Dist: e.target.value});
  }

  handleMovt = (e) =>{
    this.setState({MovtDeg: e.target.value});
  }

  render() {
    var url = this.state.img ? ("data:image/jpg;base64," + this.state.img) : null;
      return (
        <div className="container">
          <div className="row dashboard">
                  <div className="col xs12 m6 image">
                    <img src={url} />
                  </div>
                  <div className="col m4 push-m1 button-container">

                    {/* Motion Controls */}
                    <div className="row header">
                      <h5>Motion Controls</h5>
                      <button onClick ={this.ChangeAutoMode} id="auto">Auto {this.state.AutoMode ? "On" : "Off"}</button>
                      <div class="divider"></div>
                    </div>

                    {/* Head */}
                    <div className="row">
                      <div className="col m4 control-label">
                        <p>Head Turn: </p>
                      </div>
                      <div className="col m6 control-input">
                        <input type="text" onChange ={this.handleHead} id="head-turn"></input><br/>
                        <p>
                          <label>
                            <input name="head" class="with-gap" type="radio" checked={this.state.HeadDir == "L"} onChange={this.handleHeadDir} value="L"/>
                            <span>Left</span>
                          </label>
                        </p>
                        <p>
                          <label>
                            <input name="head" class="with-gap" type="radio" checked={this.state.HeadDir == "R"} onChange={this.handleHeadDir} value="R"/>
                            <span>Right</span>
                          </label>
                        </p>
                      </div>
                      <div className="col m1 control-unit"><p>deg</p></div>
                    </div>

                    {/* Body */}
                    <div className="row">
                      <div className="col m4 control-label">
                        <p>Body Turn: </p>
                      </div>
                      <div className="col m6 control-input">
                        <input type="text" onChange ={this.handleBody} id="head-turn"></input><br/>
                        <p>
                          <label>
                            <input name="group1" class="with-gap" type="radio"  />
                            <span>Left</span>
                          </label>
                        </p>
                        <p>
                          <label>
                            <input name="group1" class="with-gap" type="radio" />
                            <span>Right</span>
                          </label>
                        </p>
                      </div>
                      <div className="col m1 control-unit"><p>deg</p></div>
                    </div>

                    {/* Distance */}
                    <div className="row">
                      <div className="col m4 control-label">
                        <p>Distance: </p>
                      </div>
                      <div className="col m6 control-input">
                        <input type="text" onChange ={this.handleDist} id="head-turn"></input><br/>
                      </div>
                      <div className="col m1 control-unit"><p>cm</p></div>
                    </div>

                    {/* Movement Angle */}
                    <div className="row">
                      <div className="col m4 control-label">
                        <p>Movement Angle: </p>
                      </div>
                      <div className="col m6 control-input">
                        <input type="text" onChange ={this.handleMovt} id="head-turn"></input><br/>
                        <p>
                          <label>
                            <input name="group1" class="with-gap" type="radio"  />
                            <span>Left</span>
                          </label>
                        </p>
                        <p>
                          <label>
                            <input name="group1" class="with-gap" type="radio" />
                            <span>Right</span>
                          </label>
                        </p>
                      </div>
                      <div className="col m1 control-unit"><p>deg</p></div>
                    </div>
                  </div>
                  
          </div>
        </div>
      )
  }
}

export default Camera;
