import React, { Component } from 'react';
import './camera.css';
import { auth } from 'firebase';
import openSocket from 'socket.io-client';

class Camera extends Component {
  constructor(props) {
    super(props);
    this.state = {
      socket: openSocket('http://localhost:1337'),
      img: null,
      size: ''
    }

    let self = this
    this.state.socket.on('event', curImg => {
      this.setState({img: curImg})
    });
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      size: event.target.value
    });
  }
  
  handleSubmit(event) {
    event.preventDefault();
    
    alert(`You chose the ${this.state.size} pizza.`);
  }

  render() {
    var url = this.state.img ? ("data:image/jpg;base64," + this.state.img) : null;
      return (
        <div className="container">
          <div className="row dashboard">
                  <div className="col xs12 m9 image">
                    <img src={url} />
                    {/* <div className="row">{url}</div> */}
                  </div>
                  <div className="col m1"></div>
                  <div className="col m2 button-container">
                    <div className="row"><button className="auto">Auto Mode</button></div>

                    <form onSubmit={this.handleSubmit}>
                    <p>Select a pizza size:</p>
                    
                    <ul>
                      <li>
                        <label>
                          <input
                            type="radio"
                            value="small"
                            checked={this.state.size === "small"}
                            onChange={this.handleChange}
                          />
                          Small
                        </label>
                      </li>
                      
                      <li>
                        <label>
                          <input
                            type="radio"
                            value="medium"
                            checked={this.state.size === "medium"}
                            onChange={this.handleChange}
                          />
                          Medium
                        </label>
                      </li>

                      <li>
                        <label>
                          <input
                            type="radio"
                            value="large"
                            checked={this.state.size === "large"}
                            onChange={this.handleChange}
                          />
                          Large
                        </label>
                      </li>
                    </ul>

                    <button type="submit">Make your choice</button>
                  </form>

                    <div className="row"><button>Tracking</button></div>
                    <div className="row"><button>Follow Link</button></div>
                    <div className="row"><button>Up</button></div>
                    <div className="row"><button>Left</button></div>
                    <div className="row"><button>Right</button></div>
                    <div className="row"><button>Down</button></div>
                  </div>
                  
          </div>
        </div>
      )
  }
}

export default Camera;
