import React, { Component } from 'react';
import './camera.css';
import { auth } from 'firebase';
import openSocket from 'socket.io-client';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      socket: openSocket('http://localhost:1337'),
      img: null
    }

    let self = this
    this.state.socket.on('event', curImg => {
      this.setState({img: curImg})
    });
  }

  render() {
    var url = this.state.img ? ("data:image/jpg;base64," + this.state.img) : null;
      return (
        <div className="container">
          <div className="row button-container">
                  <div className="col s2 m9">
                    <img src={url} />
                    <div className="row">{url}</div>
                  </div>
                  <div className="col s10 m3">
                    <button className="offset-m1">Tracking</button>
                    <div className="row"></div>
                    <button className="offset-m1">Follow Link</button>
                    <div className="row"></div>
                    <button className="offset-m1">Up</button>
                    <div className="row"></div>
                    <button className="offset-m1">Left</button>
                    <div className="row"></div>
                    <button className="offset-m1">Right</button>
                    <div className="row"></div>
                    <button  className="offset-m1">Down</button>
                    <div className="row"></div>
                    <button className="offset-m1">Auto Mode</button>
                  </div>
                  
          </div>
        </div>
      )
  }
}

export default App;
